convert_to_date &lt;- function(d) {
  year &lt;- as.numeric(sub("ë…„.*", "", d)) + 2000  # 2000ì„ ë”í•´ì„œ 4ìë¦¬ ì—°ë„ë¥¼ ë§Œë“­ë‹ˆë‹¤.
  month &lt;- as.numeric(sub(".* (\\d+)ì›”.*", "\\1", d))
  week &lt;- as.numeric(sub(".*ì›” (\\d+)ì£¼", "\\1", d))
  start_date &lt;- ymd(paste(year, month, 1, sep = "-"))

  return(start_date + days(7 * (week - 1)))
}


